package com.atikfaysal.smartofficemanagement.adapters;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentStatePagerAdapter;import com.atikfaysal.smartofficemanagement.fragments.CompleteWork;import com.atikfaysal.smartofficemanagement.fragments.PendingWork;import com.atikfaysal.smartofficemanagement.fragments.RunningWork;/** * Created by USER on 2/23/2018. */public class ViewPagerAdapter extends FragmentStatePagerAdapter{     private int numberOfPage;     private String user;     public ViewPagerAdapter(FragmentManager fm, int numberOfPage)     {          super(fm);          this.numberOfPage = numberOfPage;     }     @Override     public Fragment getItem(int position) {          Fragment object = null;          switch (position) {               case 0:                    object = new RunningWork();                    break;               case 1:                    object = new PendingWork();                    break;               case 2:                    object = new CompleteWork();                    break;          }          return object;     }     @Override     public int getCount() {          return numberOfPage;     }}